import { useQuery, gql } from "@apollo/client"
import './App.css';
import { BrowserRouter, Switch, Route, NavLink } from "react-router-dom";
import Home from "./Pages/Home/Home";
import Auth from "./Pages/Auth/Auth";
import Exit from "./Pages/Exit/Exit";

const App = () => {

  const ALL_USERS = gql`
    {
      users {
        name
        id
        rocket
        timestamp
        twitter
      }
    }
  `
  const auth = localStorage.getItem("user") ? true : false

  console.log("auth ?", auth);

  const { data, error, loading, refetch, fetchMore } = useQuery(
    ALL_USERS,
    {
      notifyOnNetworkStatusChange: true,
      variables: { id: '', name: '', rocket: '', twitter: '' },
    }
  )

  if (loading) { console.log("loading..."); }
  if (error) { console.log("error", error); }
  if (data) {     console.log("DATA", data); }

  const logOut = () => {
    localStorage.clear()
    window.location.replace("/exit")
  }

  return (
    <div className="App">
      <BrowserRouter>
        <div className="nav">
          <NavLink to="/" onClick={() => auth ? null : window.location.replace("/auth")} >home</NavLink>
          {
            auth
            ? null
            : <NavLink to="/auth">auth</NavLink>
          }
          {
            auth
            ? <NavLink to="/exit" onClick={logOut}>exit</NavLink>
            : null
          }
        </div>
        <Switch>
          <Route exact path="/auth">
            <Auth/>
          </Route>
          <Route exact path={ auth ? "/" : "/auth"}>
            <Home
              search={(name, gender, birthYear) => {
                refetch({
                  id,
                  name,
                  rocket,
                  twitter
                });
              }}
            />
          </Route>
          <Route exact path="/exit">
            <Exit/>
          </Route>
        </Switch>
      </BrowserRouter>
    </div>
  )
}

export default App;
