import "./Menu.sass"

const Menu = () => {

    const auth = localStorage.getItem("user") ? true : false

    const logOut = () => {
        localStorage.clear()
        window.location.replace("/exit")
    }

    return (
        <div className="Menu">
        <NavLink to="/" onClick={() => props.auth ? null : window.location.replace("/auth")} >home</NavLink>
        {
          auth
          ? null
          : <NavLink to="/auth">auth</NavLink>
        }
        {
          auth
          ? <NavLink to="/exit" onClick={logOut}>exit</NavLink>
          : null
        }
      </div>
    )
}

export default Menu;
