import "./Home.sass"
import React, { useCallback, useState, useRef, useEffect } from 'react';
import { set } from "lodash";
// import debounce from 'lodash/debounce';

const Home  = ( props ) => {

    function useDidUpdate(callback, deps) {
        const hasMount = useRef(false);

        useEffect(() => {
          if (hasMount.current) {
            callback();
          } else {
            hasMount.current = true;
          }
        }, deps);
      }

    // filters

    const [name, setName] = useState('');
    const [rocket, setRocket] = useState('');
    const [twitter, setTwitter] = useState('');

    const callSearch = useCallback(
      (name, rocket, twitter) => {
        props.search(name, rocket, twitter);
      },
      []
    );
    // const debouncedSearch = useCallback(debounce(callSearch, 200), []);


    // // Call search on input changes
    // useDidUpdate(() => {
    //   debouncedSearch(name, rocket, twitter);
    // }, [name, rocket, twitter]);

    useDidUpdate(() => callSearch(name, rocket, twitter), [
            name,
            rocket,
            twitter
    ]);

    const[history] = useState({name: [], rocket: [], twitter: []})
    const[tempArr] = useState([])

    let cleanItems;
    const cleanArr = (arr) => {
        return cleanItems = arr.filter((e, i, a) => a.indexOf(e) === i)
    }

    const searchName = e => {
      let value = e.target.value
      setName(value)
      tempArr.push(value)
      setTimeout(() => {
        tempArr.splice(0, tempArr.length - 1)
        cleanArr(tempArr)
        return history.name.push(cleanItems[0])
      }, 4000)
      console.log("his", history);
    }


    return (
        <div className="Home">
            <div className="historyBar">
                History :
            </div>
            <div className="main">
                <div className="search">
                    Search
                    <input placeholder="name" onChange={e => searchName(e)}/>
                    <input placeholder="rocket" onChange={e => setRocket(e.target.value)}/>
                    <input placeholder="twitter" onChange={e => setTwitter(e.target.value)}/>
                </div>
                <div className="results">

                    {
                      props.loading
                        ? <div style={{fontSize: 35, color: "white"}}>Loading ...</div>
                        : null
                    }

                    {
                      props.error
                        ? <div
                            style={{
                              fontSize: 25,
                              color: "white",
                              width: 400,
                              height: 150,
                              border: "1px solid white",
                              overflow: "scroll"
                            }}
                            >
                              {props.error}
                          </div>
                        : null
                    }

                    {
                        props.data?.users && !props.loading && !props.error
                        ? props.data.users.map( (user, id) => {
                            return (
                                <div key={id} className="resultContainer">
                                    <div className="resultItem">id: {user.id}</div>
                                    <div className="resultItem">name: {user.name}</div>
                                    <div className="resultItem">rocket: {user.rocket}</div>
                                    <div className="resultItem">twitter: {user.twitter}</div>
                                    <div className="resultItem">timestamp: {user.timestamp}</div>
                                </div>
                            )
                        })
                        : null
                    }


                </div>
            </div>
        </div>
    )
}

export default Home
