import "./Home.sass"
import React, { useCallback, useState, useRef, useEffect } from 'react';
// import debounce from 'lodash/debounce';

const Home  = ( props ) => {

    function useDidUpdate(callback, deps) {
        const hasMount = useRef(false);

        useEffect(() => {
          if (hasMount.current) {
            callback();
          } else {
            hasMount.current = true;
          }
        }, deps);
      }

    // filters

    const [name, setName] = useState('');
    const [rocket, setRocket] = useState('');
    const [twitter, setTwitter] = useState('');

    const callSearch = useCallback(
      (name, rocket, twitter) => {
        props.search(name, rocket, twitter);
      },
      []
    );
    // const debouncedSearch = useCallback(debounce(callSearch, 200), []);


    // // Call search on input changes
    // useDidUpdate(() => {
    //   debouncedSearch(name, rocket, twitter);
    // }, [name, rocket, twitter]);

    useDidUpdate(() => callSearch(name, rocket, twitter), [
            name,
            rocket,
            twitter
    ]);


    return (
        <div className="Home">
            <div className="historyBar">
                History :
            </div>
            <div className="main">
                <div className="search">
                    Search
                    <input placeholder="name" onChange={e => setName(e.target.value)}/>
                    <input placeholder="rocket" onChange={e => setRocket(e.target.value)}/>
                    <input placeholder="twitter" onChange={e => setTwitter(e.target.value)}/>
                </div>
                <div className="results">

                  {
                    props.loading
                      ? <div style={{fontSize: 35}}>Loading ...</div>
                      : null
                  }

                  {
                    props.error
                      ? <div style={{fontSize: 25, width: 400, height: 150, border: "1px solid white", overflow: "scroll"}}>{props.error}</div>
                      : null
                  }


                    {
                        props.data?.users
                        ? props.data.users.map( user => {
                            return (
                                <div className="resultContainer">
                                    <div className="resultItem">id: {user.id}</div>
                                    <div className="resultItem">name: {user.name}</div>
                                    <div className="resultItem">rocket: {user.rocket}</div>
                                    <div className="resultItem">twitter: {user.twitter}</div>
                                    <div className="resultItem">timestamp: {user.timestamp}</div>
                                </div>
                            )
                        })
                        : null
                    }


                </div>
            </div>
        </div>
    )
}

export default Home
