import "./Home.css"
import React, { useCallback, useState } from 'react';
import debounce from 'lodash/debounce';
import useDidUpdate from '../utils/useDidUpdate';

const Home  = () => {

    // filters

    const [name, setName] = useState('');
    const [id, setId] = useState('');
    const [rocket, setRocket] = useState('');
    const [twitter, setTwitter] = useState(false);

    const callSearch = useCallback(
      (name, id, rocket, twitter) => {
        search(name, id, rocket, twitter);
      },
      []
    );
    const debouncedSearch = useCallback(debounce(callSearch, 200), []);

    // Call search on input changes
    useDidUpdate(() => {
      debouncedSearch(name, id, rocket, twitter);
    }, [name, id, rocket, twitter]);
    useDidUpdate(() => callSearch(name, id, rocket, twitter), [
        name,
        id,
        rocket,
        twitter
    ]);debouncedSearch


    return (
        <div className="Home">
            <div className="historyBar"></div>
            <div className="main">
                <div className="search">
                    filters
                    <input className="id" onClick={e => setId(e.target.value)}/>
                    <input className="name" onClick={e => setName(e.target.value)}/>
                    <input className="rocket" onClick={e => setRocket(e.target.value)}/>
                    <input className="twitter" onClick={e => setTwitter(e.target.value)}/>
                </div>
                <div className="results"></div>
            </div>
        </div>
    )
}

export default Home
