import "./Home.css"
import React, { useCallback, useState, useRef, useEffect } from 'react';
import debounce from 'lodash/debounce';

const Home  = ( {search} ) => {

    function useDidUpdate(callback, deps) {
        const hasMount = useRef(false);

        useEffect(() => {
          if (hasMount.current) {
            callback();
          } else {
            hasMount.current = true;
          }
        }, deps);
      }

    // filters

    const [name, setName] = useState('');
    const [id, setId] = useState('');
    const [rocket, setRocket] = useState('');
    const [twitter, setTwitter] = useState('');

    const callSearch = useCallback(
      (name, rocket, twitter) => {
        search(name, rocket, twitter);
      },
      []
    );
    const debouncedSearch = useCallback(debounce(callSearch, 200), []);


    // Call search on input changes
    useDidUpdate(() => {
      debouncedSearch(name, id, rocket, twitter);
    }, [name, id, rocket, twitter]);

    useDidUpdate(() => callSearch(name, id, rocket, twitter), [
            name,
            id,
            rocket,
            twitter
    ]);

    // console.log("HOME DATA", props.data.users.map( d => d));


    return (
        <div className="Home">
            <div className="historyBar"></div>
            <div className="main">
                <div className="search">
                    filters
                    <input placeholder="id" onChange={e => setId(e.target.value)}/>
                    <input placeholder="name" onChange={e => setName(e.target.value)}/>
                    <input placeholder="rocket" onChange={e => setRocket(e.target.value)}/>
                    <input placeholder="twitter" onChange={e => setTwitter(e.target.value)}/>
                </div>
                <div className="results"></div>
            </div>
        </div>
    )
}

export default Home
